import React, { Component } from 'react'
import util from '../util'
import './Characteristics.css';

export default class Characteristics extends Component {
    render() {
        const {characteristicsItems} = this.props;
        return (
            <div className="alert alert-info text-center">
                {characteristicsItems.length === 0 
                ? "No phones selected": 
                <div>You have {characteristicsItems.length} phones selected.<hr /></div>}
                {characteristicsItems.length > 0 && 

                    <div>
                        <div >
                            {characteristicsItems.map(item =>

                                <li className="img-thumbnail">
                                    <div className ="loader">
                                    <h4><b> <p className=" text-success "><u>Phone</u>  </p> {item.name}</b></h4>
                                    <h5><b> <p className=" text-success "><u>Manufacturer</u> </p> {item.manufacturer}</b></h5>
                                    <h5><b> <p className=" text-success text-justify "><u>Description</u> </p> {item.description}</b></h5>
                                    <h5><b> <p className=" text-success "><u>Color</u></p>  {item.color}</b></h5>
                                    <h5><b> <p className=" text-success "><u>Screen</u> </p> {item.screen}</b></h5>
                                    <h5><b> <p className=" text-success "><u>Processor</u> </p> {item.processor}</b></h5>
                                    <h5><b> <p className=" text-success "><u>Ram memory</u> </p> {item.ram} Gb</b></h5>
                                    </div>
                                    <button style={{ float: 'right' }} className="btn btn-danger btn-xs"
                                        onClick={(e) => this.props.handleRemoveFromCharacteristics(e, item)}>X</button>
                                    <hr />
                                </li>)}
                        </div>
                    </div>

                }

            </div>
        )
    }
}
