import React, { Component } from 'react';
import util from '../util.js';
import './Phones.css';
export default class Phones extends Component {
    render() {
        const phoneItems = this.props.phones.map( phone => (
         <div className="col-md-6" key = {phone.id}>
             <div className="thumbnail text-center pt-4 ">
                <a  href={`#${phone.id}`}  onClick={(e)=> this.props.handleAddCharacteristics(e,phone)}>
                    <img className= "responsive__img" src={`/phones/${phone.id}.jpg`} alt={phone.name}/>                     
                </a>
                <div>
                    <div>
                    <p>{phone.name} </p>
                    <b className= "text-success">Price: {util.formatCurrency(phone.price)} </b> 
                    </div>

                    <button className="btn btn-primary"
                        onClick={(e)=> this.props.handleAddCharacteristics(e,phone)}
                    > Characteristics </button>
                </div>
            </div>
         </div>   )

        )

        return (
            <div className="row">
                {phoneItems}
            </div>
        )
    }
}
