import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Phones from './components/Phones';
import Filter from './components/Filter';

class App extends Component {
constructor(props){
  super(props)
  this.state ={phones :[],filteredPhones:[],}
  this.handleChangeSort = this.handleChangeSort.bind(this);
  this.handleChangeColor = this.handleChangeColor.bind(this);
}
componentWillMount(){
  fetch("http://localhost:8000/phones").then(res => res.json())
  .then(data => this.setState({
    phones:data,
    filteredPhones: data
  }));
}

handleChangeSort(e){
  this.setState({sort: e.target.value});
  this.listPhones();
}

handleChangeColor(e){
  this.setState({color: e.target.value});
  this.listPhones();
}

listPhones(){
  this.setState(state => {
    if(state.sort !== ''){
      state.phones.sort((a,b) => (state.sort === 'lowest')? (a.price > b.price?1:-1): (a.price < b.price?1:-1)) 
    } else{
      state.phones.sort((a,b) => (a.id < b.id?1:-1));
    }

    if (state.color !== '') {
      return { filteredPhones: state.phones.filter(a => a(state.color.toLowerCase()) >= 0) };
    }

    return {filteredPhones:state.phones}
  })
}


render (){
  return (
    <div className="container">

    <div className="text-center img-fluid">
    <br/>  
    <img className="header__image" src="https://images.ctfassets.net/5gv1edeicqfs/bomBQDobMA6eyu4CkuYmM/6f5debe74cf1e335bb0be7e3ecbba40b/gs-logo.png"/> 
    </div>

    <h3>Phone Catalogue </h3>

      <hr/>
      <div className="row ">
        <div className="col-md-6">

        <Filter color ={this.state.color} sort={this.state.sort} 
        handleChangeColor={this.handleChangeColor} handleChangeSort={this.handleChangeSort} 
        count ={this.state.filteredPhones.length}/>
        <hr/>
        <Phones phones={this.state.filteredPhones} handleAddCharacteristics={this.handleAddCharacteristics}/>
        </div>
        <div className="col-md-6">

        </div>
      </div>

    </div>
  );
}
}


export default App;
