import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Phones from './components/Phones';
import Filter from './components/Filter';
import Characteristics from './components/Characteristics';
class App extends Component {
constructor(props){
  super(props)
  this.state ={phones :[],filteredPhones:[], characteristicsItems:[]};
  this.handleChangeSort = this.handleChangeSort.bind(this);
  this.handleAddCharacteristics = this.handleAddCharacteristics.bind(this);

}
componentWillMount(){
  fetch("http://localhost:8000/phones").then(res => res.json())
  .then(data => this.setState({
    phones:data,
    filteredPhones: data
  }));
}

handleChangeSort(e){
  this.setState({sort: e.target.value});
  this.listPhones();
}

handleChangeColor(e){
  this.setState({color: e.target.value});
  this.listPhones();
}

listPhones(){
  this.setState(state => {
    if(state.sort !== ''){
      state.phones.sort((a,b) => (state.sort === 'lowest')? (a.price > b.price?1:-1): (a.price < b.price?1:-1)) 
    } else{
      state.phones.sort((a,b) => (a.id < b.id?1:-1));
    }

  })
}

handleAddCharacteristics(e,phone){
  this.setState(state => {
    const characteristicsItems = state.characteristicsItems;
    let phoneAlreadyInCharacteristics = false;
    characteristicsItems.forEach(item => {

      if(item.id ===phone.id){
        phoneAlreadyInCharacteristics = true; 
        item.count++;
      }
    });    

    if(!phoneAlreadyInCharacteristics){
      characteristicsItems.push({...phone,count:1});
    }
    localStorage.setItem("characteristicsItems",JSON.stringify(characteristicsItems));
    return characteristicsItems;

  })
}

render (){
  return (
    <div className="container">

    <div className="text-center img-fluid">
    <br/>  
    <img className="header__image" src="https://images.ctfassets.net/5gv1edeicqfs/bomBQDobMA6eyu4CkuYmM/6f5debe74cf1e335bb0be7e3ecbba40b/gs-logo.png"/> 
    </div>

    <h3>Phone Catalogue </h3>

      <hr/>
      <div className="row ">
        <div className="col-md-6">

        <Filter  sort={this.state.sort} handleChangeSort={this.handleChangeSort} count ={this.state.filteredPhones.length}/>
        <hr/>
        <Phones phones={this.state.filteredPhones} handleAddCharacteristics={this.handleAddCharacteristics}/>
        </div>
        <div className="col-md-6">
        <Characteristics characteristicsItems={this.state.characteristicsItems}
        handleRemoveFromCharacteristics={this.handleRemoveCharacteristics}/>
        </div>
      </div>

    </div>
  );
 }
}


export default App;
